// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "./rxgToken.sol";

contract Marketplace is ReentrancyGuard {
    using Counters for Counters.Counter;
    Counters.Counter private _items;
    Counters.Counter private _soldItems;
// variables
    address payable public liquidityPool;
    address payable public owner;
    Recharge public dc;
    uint256 public minlistingPrice = 0.5 ether; // minimum price in MATIC

    // marketplace item
    struct MarketplaceItem {
        uint256 saleID;
        address nftContract;
        uint256 tokenId;
        address payable seller;
        uint256 price;
        uint256 tokenSalePrice;
        bool sold;
         bool canceled;
    }


// modifiers
    modifier onlySeller(uint256 saleID) {
        require(msg.sender == idToMarketplaceItem[saleID].seller, "Only seller");
        _;
    }

    modifier onlyOwner() {
        require(msg.sender == owner, "Only contract owner");
        _;
    }

    modifier onlyNotSoldOrCanceled(uint256 saleID) {
        require(!idToMarketplaceItem[saleID].sold || !idToMarketplaceItem[saleID].canceled, "item is sold or canceled");
        _;
    }
// mapping
    mapping(uint256 => MarketplaceItem) private idToMarketplaceItem;
// events
    // declare a event for when a item is created on marketplace
    event MarketplaceItemCreated(
        uint256 indexed saleID,
        address indexed nftContract,
        uint256 indexed tokenId,
        address seller,
        uint256 price,
        uint256 tokenSalePrice,
        bool sold,
        bool canceled
    );
    // declare a event for when a item is returned to the seller
    event MarketplaceItemReturned(
        uint256 indexed saleID,
        address indexed nftContract,
        uint256 indexed tokenId,
        address seller,
        uint256 price,
        bool sold,
        bool canceled
    );
    // declare a event for when a item is sold
    event MarketplaceItemSold(
        uint256 indexed saleID,
        address indexed nftContract,
        uint256 indexed tokenId,
        address seller,
        uint256 price,
        bool sold,
        bool canceled
    );
// constructor
    // the payout wallet is the DAO wallet and cannot be changed after deployment
    constructor(address payable wallet, Recharge token) payable{
        liquidityPool = wallet;
        dc = token;
    }

// functions
    // returns the listing price of the contract
    function getListingPrice(uint256 _saleID) public view returns (uint256) {
        return idToMarketplaceItem[_saleID].price;
        }

    // places an item for sale on the marketplace
    function createMarketplaceItem(
        address nftContract,
        uint256 tokenId,
        uint256 price,
        uint256 tokenSalePrice
        ) public payable nonReentrant {
            require(price > minlistingPrice, "Price must be at least 0.5 MATIC");

            _items.increment();
            uint256 saleID = _items.current();

            idToMarketplaceItem[saleID] = MarketplaceItem(
                saleID,
                nftContract,
                tokenId,
                payable(msg.sender),
                price,
                tokenSalePrice,
                false,
                false
            );

            // nft is transferred to the marketplace so it can't be rugged
            IERC721(nftContract).transferFrom(msg.sender, address(this), tokenId);

            emit MarketplaceItemCreated(
                saleID,
                nftContract,
                tokenId,
                msg.sender,
                price,
                tokenSalePrice,
                false,
                false
            );
        }
    // sale, transfers ownership of the item, as well as payment
    function maticNFTpurchase(uint256 saleID)
        public
        payable
        nonReentrant
        onlyNotSoldOrCanceled(saleID)
        {
            require(msg.value == idToMarketplaceItem[saleID].price, "value must equal selling price");
            uint256 _fee = msg.value / 100; // fee is 1% of the payment

            uint256 tokenId = idToMarketplaceItem[saleID].tokenId;
            address nftContract = idToMarketplaceItem[saleID].nftContract;

            idToMarketplaceItem[saleID].sold = true;
            // forward the matic recieved from msg.sender to the seller minus the fee
            idToMarketplaceItem[saleID].seller.transfer(msg.value - _fee);
            // contract matic balance increases by the fee
            liquidityPool.transfer(_fee);
            IERC721(nftContract).transferFrom(address(this), msg.sender, tokenId);
            _soldItems.increment();
        }

    // returns list of all unsold marketplace items
    function fetchMarketplaceItems()
        public
        view
        returns (MarketplaceItem[] memory)
        {
            uint256 itemCount = _items.current();
            uint256 unsoldItemCount = _items.current() - _soldItems.current();
            uint256 currentIndex = 0;

            MarketplaceItem[] memory items = new MarketplaceItem[](unsoldItemCount);
            for (uint256 i = 0; i < itemCount; i++) {
                if (idToMarketplaceItem[i + 1].seller == address(0)) {
                    uint256 currentId = i + 1;
                    MarketplaceItem storage currentItem = idToMarketplaceItem[
                        currentId
                    ];
                    items[currentIndex] = currentItem;
                    currentIndex += 1;
                }
            }
            return items;
        }

    // returns only items that a user has purchased
    function fetchMyNFTs() public view returns (MarketplaceItem[] memory) {
        uint256 totalItemCount = _items.current();
        uint256 itemCount = 0;
        uint256 currentIndex = 0;

        for (uint256 i = 0; i < totalItemCount; i++) {
            if (idToMarketplaceItem[i + 1].seller == msg.sender) {
                itemCount += 1;
            }
        }

        MarketplaceItem[] memory items = new MarketplaceItem[](itemCount);
        for (uint256 i = 0; i < totalItemCount; i++) {
            if (idToMarketplaceItem[i + 1].seller == msg.sender) {
                uint256 currentId = i + 1;
                MarketplaceItem storage currentItem = idToMarketplaceItem[
                    currentId
                ];
                items[currentIndex] = currentItem;
                currentIndex += 1;
            }
        }
        return items;
        }
    // returns only items a user has created
    function fetchItemsCreated()
        public
        view
        returns (MarketplaceItem[] memory)
        {
            uint256 totalItemCount = _items.current();
            uint256 itemCount = 0;
            uint256 currentIndex = 0;

            for (uint256 i = 0; i < totalItemCount; i++) {
                if (idToMarketplaceItem[i + 1].seller == msg.sender) {
                    itemCount += 1;
                }
            }

        MarketplaceItem[] memory items = new MarketplaceItem[](itemCount);

        for (uint256 i = 0; i < totalItemCount; i++) {
            if (idToMarketplaceItem[i + 1].seller == msg.sender) {
                uint256 currentId = i + 1;
                MarketplaceItem storage currentItem = idToMarketplaceItem[
                    currentId
                ];
                items[currentIndex] = currentItem;
                currentIndex += 1;
            }
        }

        return items;
        }
    // if the user cancels a sale or the timer runs out return the item to the owner
    function cancelMarketplaceSale(uint256 saleID) 
        public 
        nonReentrant 
        onlySeller(saleID)
        onlyNotSoldOrCanceled(saleID) 
        {
        MarketplaceItem storage item = idToMarketplaceItem[saleID];
        require(
            item.sold == false,
            "Can't cancel sold item"
        );
        // set canceled to true
        idToMarketplaceItem[saleID].canceled = true;
        // transfer ownership back to the seller and require them to pay the gas

        IERC721(item.nftContract).transferFrom(address(this), msg.sender, idToMarketplaceItem[saleID].tokenId);
        // emit event
        emit MarketplaceItemReturned(
            saleID,
            item.nftContract,
            item.tokenId,
            item.seller,
            item.price,
            item.sold,
            item.canceled
        );
        }
    // return the matic ballance of the marketplace contract
    function getMaticBalance() public view returns (uint256) {
        return address(this).balance;
        }
    // withdraw spill funds from the marketplace
    function withdrawFunds() public payable onlyOwner {
        require(address(this).balance > 0, "No funds to withdraw");
        liquidityPool.transfer(address(this).balance);
        }
    // purchase nft with ERC20
    function tokenNFTpurchase(uint256 saleID)
        public
        payable
        nonReentrant
        onlyNotSoldOrCanceled(saleID)
        {
            MarketplaceItem storage item = idToMarketplaceItem[saleID];
            require(
                item.sold == false,
                "Can't purchase sold item"
            );
            // must authorize the purchase
            require(dc.transferFrom(msg.sender, idToMarketplaceItem[saleID].seller, idToMarketplaceItem[saleID].tokenSalePrice), "Token transfer failed");
            // transfer ownership of the item
            IERC721(item.nftContract).transferFrom(address(this), msg.sender, idToMarketplaceItem[saleID].tokenId);
            // set sold to true
            idToMarketplaceItem[saleID].sold = true;
            // emit event
            emit MarketplaceItemCreated(
                saleID,
                item.nftContract,
                item.tokenId,
                msg.sender,
                item.price,
                item.tokenSalePrice,
                false,
                false
            );
        }
}
